object Solution {
    def minOperations(nums: List[Int], target: Int): Int = {
        var sum = nums.sum
        if (sum < target) return -1
        if (sum == target) return 0

        val count = Array.ofDim[Int](31)
        nums.foreach(n => count((Math.log(n) / Math.log(2)).toInt) += 1)

        var ops = 0
        var currTarget = target

        for (i <- 30 to 0 by -1) {
            val value = 1 << i
            while (count(i) > 0 && currTarget >= value) {
                currTarget -= value
                count(i) -= 1
                sum -= value
                if (currTarget <= sum) return ops
            }
            if (i > 0 && count(i) > 0) {
                val neededOps = Math.min(count(i), (currTarget + value - 1) / value)
                ops += neededOps
                currTarget -= neededOps * value
                sum -= neededOps * value
                if (currTarget <= 0) return ops
                count(i - 1) += 2 * neededOps
            }
        }
        if (currTarget > 0) -1 else ops
    }
}
