function minOperations(nums: number[], target: number): number {
    const count = new Map<number, number>();
    let operations = 0, currentSum = 0;

    for (const num of nums) {
        currentSum += num;
        count.set(num, (count.get(num) || 0) + 1);
    }

    if (currentSum < target) return -1;
    if (currentSum === target) return 0;

    for (let bit = 30; bit >= 0; bit--) {
        const val = 1 << bit;
        while (count.get(val) > 0 && currentSum - val >= target) {
            operations++;
            currentSum -= val;
            count.set(val, count.get(val) - 1);
            const half = val / 2;
            count.set(half, (count.get(half) || 0) + 2);
        }
    }

    return currentSum === target ? operations : -1;
}
