public class Solution {
    public int MinimumTime(IList<int> nums1, IList<int> nums2, int x) {
        int n = nums1.Count;
        long totalSum = 0;
        for (int i = 0; i < n; i++) {
            totalSum += nums1[i];
        }
        if (totalSum <= x) return 0;

        // Prefix sums of nums2 for binary search
        long[] prefixSums = new long[n + 1];
        for (int i = 1; i <= n; i++) {
            prefixSums[i] = prefixSums[i - 1] + nums2[i - 1];
        }

        long minTime = long.MaxValue;
        for (int i = 0; i < n; i++) {
            long target = totalSum - x;
            int left = 0, right = n;
            while (left <= right) {
                int mid = left + (right - left) / 2;
                long gain = nums1[i] + nums2[i] * (long)mid;
                if (gain >= target + prefixSums[mid]) {
                    minTime = Math.Min(minTime, mid + 1L); // Plus one for the operation itself
                    right = mid - 1;
                } else {
                    left = mid + 1;
                }
            }
        }

        return minTime == long.MaxValue ? -1 : (int)minTime;
    }
}
