#include <vector>
#include <algorithm>
using namespace std;

class Solution {
public:
    int minimumTime(vector<int>& nums1, vector<int>& nums2, int x) {
        int n = nums1.size();
        vector<long long> prefixSum(n + 1, 0);
        for (int i = 0; i < n; ++i) {
            prefixSum[i + 1] = prefixSum[i] + nums1[i];
        }
        if (prefixSum[n] <= x) return 0;

        long long total = prefixSum[n];
        vector<long long> maxAfter(n + 1, 0);
        for (int i = n - 1; i >= 0; --i) {
            maxAfter[i] = max(maxAfter[i + 1], (long long)nums2[i]);
        }

        int ans = INT_MAX;
        long long curr = 0;
        for (int i = 0; i < n; ++i) {
            curr += nums2[i];
            int left = i + 1, right = n, best = n;
            while (left <= right) {
                int mid = left + (right - left) / 2;
                if (total - (prefixSum[mid] - prefixSum[i + 1]) + curr <= x) {
                    best = mid;
                    right = mid - 1;
                } else {
                    left = mid + 1;
                }
            }
            ans = min(ans, best);
            if (total - prefixSum[i + 1] + curr <= x) {
                ans = min(ans, i + 1);
                break;
            }
        }

        return ans == INT_MAX ? -1 : ans;
    }
};
