#include <vector>
#include <algorithm>

class Solution {
public:
    int minimumTime(vector<int>& nums1, vector<int>& nums2, int x) {
        int n = nums1.size();
        vector<long long> prefSum1(n + 1, 0), prefSum2(n + 1, 0);
        for (int i = 0; i < n; ++i) {
            prefSum1[i + 1] = prefSum1[i] + nums1[i];
            prefSum2[i + 1] = prefSum2[i] + nums2[i];
        }

        if (prefSum1[n] <= x) return 0;

        int ans = INT_MAX;
        for (int i = 0; i <= n; ++i) {
            int lo = 0, hi = n - i, mid, time;
            while (lo <= hi) {
                mid = lo + (hi - lo) / 2;
                if (prefSum1[mid] + prefSum2[n] - prefSum2[n - i] <= x) {
                    time = mid + i;
                    ans = min(ans, time);
                    hi = mid - 1;
                } else {
                    lo = mid + 1;
                }
            }
        }

        return ans == INT_MAX ? -1 : ans;
    }
};
