#include <stdlib.h>

int minimumTime(int* nums1, int nums1Size, int* nums2, int nums2Size, int x){
    long long total = 0;
    for (int i = 0; i < nums1Size; i++) {
        total += nums1[i];
    }
    if (total <= x) return 0;

    long long *gain = (long long *)malloc(nums1Size * sizeof(long long));
    for (int i = 0; i < nums1Size; i++) {
        gain[i] = (long long)nums1[i] + (long long)nums2[i] * (nums1Size - 1);
    }

    for (int time = 1; time <= nums1Size; time++) {
        long long maxGain = 0;
        int index = -1;
        for (int i = 0; i < nums1Size; i++) {
            if (gain[i] > maxGain) {
                maxGain = gain[i];
                index = i;
            }
        }
        if (index == -1) break;
        total -= nums1[index];
        if (total <= x) {
            free(gain);
            return time;
        }
        gain[index] = 0; // This index won't be chosen again
        for (int j = 0; j < nums1Size; j++) {
            if (j != index) {
                gain[j] -= nums2[j]; // Adjust the gain since we can reduce one less second
            }
        }
    }
    free(gain);
    return -1;
}
