class Solution {
    fun minimumTime(nums1: List<Int>, nums2: List<Int>, x: Int): Int {
        var totalSum = nums1.sum()
        if (totalSum <= x) return 0

        val n = nums1.size
        val prefixSums = MutableList(n + 1) { 0 }
        for (i in 1..n) {
            prefixSums[i] = prefixSums[i - 1] + nums1[i - 1]
        }

        var bestTime = Int.MAX_VALUE
        for (i in 0 until n) {
            var low = i
            var high = n
            while (low < high) {
                val mid = (low + high) / 2
                val sumAfterOperations = prefixSums[i] + (prefixSums[n] - prefixSums[mid + 1]) + nums2.subList(i, mid + 1).sum() * (mid - i + 1)
                if (sumAfterOperations <= x) {
                    bestTime = minOf(bestTime, mid - i + 1)
                    high = mid
                } else {
                    low = mid + 1
                }
            }
        }

        return if (bestTime == Int.MAX_VALUE) -1 else bestTime
    }
}
