class Solution {
    fun minimumTime(nums1: List<Int>, nums2: List<Int>, x: Int): Int {
        val n = nums1.size
        val prefix = IntArray(n + 1)
        val suffix = IntArray(n + 1)
        for (i in nums1.indices) {
            prefix[i + 1] = prefix[i] + nums1[i]
        }
        for (i in nums1.indices.reversed()) {
            suffix[i] = suffix[i + 1] + nums1[i]
        }
        var total = prefix[n]
        if (total <= x) return 0
        var minTime = Int.MAX_VALUE
        for (i in 0..n) {
            var left = 0
            var right = n
            while (left <= right) {
                val mid = (left + right) / 2
                val current = prefix[i] + suffix[n - mid]
                val increment = nums2.subList(n - mid, n).sumOf { it * (i + mid - n) }
                if (current + increment <= x) {
                    minTime = minOf(minTime, i + mid - n)
                    right = mid - 1
                } else {
                    left = mid + 1
                }
            }
        }
        return if (minTime == Int.MAX_VALUE) -1 else minTime
    }
}
