object Solution {
    def minimumTime(nums1: List[Int], nums2: List[Int], x: Int): Int = {
        val n = nums1.length
        var prefixSum = Array.fill(n + 1)(0)
        for (i <- nums1.indices) {
            prefixSum(i + 1) = prefixSum(i) + nums1(i)
        }
        if (prefixSum(n) <= x) return 0

        var bestTime = Int.MaxValue
        var suffixSum = 0
        for (i <- n - 1 to 0 by -1) {
            suffixSum += nums2(i)
            var left = 0
            var right = i
            while (left <= right) {
                val mid = left + (right - left) / 2
                if (prefixSum(mid) + suffixSum * (mid + 1) <= x) {
                    bestTime = bestTime.min(mid + 1 + n - i)
                    left = mid + 1
                } else {
                    right = mid - 1
                }
            }
        }

        if (bestTime == Int.MaxValue) -1 else bestTime
    }
}
