function minimumTime(nums1: number[], nums2: number[], x: number): number {
    let totalSum = nums1.reduce((a, c, i) => a + c + nums2[i], 0);
    if (totalSum <= x) return 0;
    let prefixSums = new Array(nums1.length + 1).fill(0);
    for (let i = 0; i < nums1.length; i++) {
        prefixSums[i + 1] = prefixSums[i] + nums1[i] + nums2[i];
    }
    let ans = Number.MAX_SAFE_INTEGER;
    for (let i = 0; i < nums1.length; i++) {
        let left = i, right = nums1.length, best = -1;
        while (left <= right) {
            let mid = (left + right) >> 1;
            if (prefixSums[mid + 1] - prefixSums[i] <= x) {
                best = mid;
                left = mid + 1;
            } else {
                right = mid - 1;
            }
        }
        if (best !== -1) {
            ans = Math.min(ans, nums1.length - (best - i + 1) + i * 2);
        }
    }
    return ans === Number.MAX_SAFE_INTEGER ? -1 : ans;
};
