public class Solution {
    public int NumberOfWays(string s, string t, long k) {
        const int mod = 1000000007;
        int n = s.Length;
        if (s == t && k % 2 == 0) return (n - 1) % mod;
        if (s == t && k % 2 == 1) return (2 * (n - 1)) % mod;
        if (k == 1) return 0;

        int[] prefix = new int[n + 1];
        for (int i = 0; i < n; i++) {
            prefix[i + 1] = prefix[i] + (s[i] == t[i] ? 1 : 0);
        }
        if (prefix[n] == n) return (n - 1) % mod; // s equals t

        for (int i = 1; i < n; i++) {
            if (s.Substring(i) + s.Substring(0, i) == t) {
                if (k == 2) return 1;
                if (k % 2 == 0) return 2;
                return 0;
            }
        }
        return 0;
    }
}
