public class Solution {
    public int NumberOfWays(string s, string t, long k) {
        const int MOD = 1000000007;
        if (s == t) {
            if (k % 3 == 0) return 1;
            if (k % 3 == 1) return 0;
            return (int)((2 * (k % 3)) % MOD);
        }

        if (k == 1) return 0;

        int n = s.Length;
        bool isEqualAfterOneOperation = false;
        for (int i = 1; i < n; i++) {
            if ((s.Substring(i) + s.Substring(0, i)).Equals(t)) {
                isEqualAfterOneOperation = true;
                break;
            }
        }

        if (!isEqualAfterOneOperation) return 0;
        if (k == 2) return 1;

        long ways = 2; // Since s can become t in exactly 2 operations if isEqualAfterOneOperation is true
        if (k > 2) {
            if (k % 2 == 0) {
                ways = 2 * (k - 1) % MOD;
            } else {
                ways = (2 * ((k - 1) / 2) % MOD) + 1;
            }
        }

        return (int)(ways % MOD);
    }
}
