public class Solution {
    public int NumberOfWays(string s, string t, long k) {
        long mod = 1000000007;
        int n = s.Length;
        if (s == t) {
            // When s equals t, we can only return to s if k is even, through any of the n-1 rotations.
            if (k % 2 == 0) return (int) (((k / 2) % mod * (n - 1) % mod) % mod);
            else return 0;
        } else {
            // For s not equal to t, we can directly transform s into t by rotating s to match t.
            // The problem is equivalent to counting ways to pick rotations that sum up to a multiple of n.
            if (k == 1) return 0; // Cannot transform s into t in exactly one move if they're not initially equal.
            if (k == 2) return 1; // Exactly one way to transform s into t by rotating once.

            // For k > 2, there are 2 ways to achieve the transformation in odd steps, and for even k, it depends on n.
            if (k % 2 == 1) return 2; // For odd k > 2, can rotate s to t and back, but not to s itself.
            else {
                // For even k, the scenario is more complex due to the possibility of returning to the original string s.
                // Since s != t, we focus on reaching t in k steps, which is always possible in 2 ways for even k.
                return (int) ((2 + (k - 3) % mod * (n - 1) % mod) % mod);
            }
        }
    }
}
