#include <bits/stdc++.h>
using namespace std;

class Solution {
public:
    int numberOfWays(string s, string t, long long k) {
        int MOD = 1000000007;
        int n = s.size();
        if (s == t && k % 2 == 0) return (k * n / 2) % MOD;
        if (s == t && k % 2 == 1) return 0;

        long long ans = 0;
        for (int i = 0; i < n; ++i) {
            if (s[i] != t[i]) ans++;
        }

        if (ans == 0) return (n * (k % 2 ? 0 : 1)) % MOD;
        if (k == 1) return 0;

        long long ways = 1;
        for (int i = 1; i <= k; ++i) {
            ways = (ways * n) % MOD;
        }

        return (ways - (k % 2 ? 0 : n) + MOD) % MOD;
    }
};
