#include <string.h>
#define MOD 1000000007

int numberOfWays(char* s, char* t, long long k) {
    int n = strlen(s);
    if (k == 1) {
        int same = strcmp(s, t) == 0 ? 1 : 0;
        return (same + 1) % MOD;
    } else if (k == 2) {
        int count = 0;
        for (int i = 0; i < n; ++i) {
            int j = (i + n / 2) % n;
            if (s[i] == t[0] && s[j] == t[n / 2]) count++;
        }
        return count % MOD;
    } else {
        // For k > 2, it's always possible to transform s into t in any way we want,
        // so we have n-1 choices in the first operation, and then n choices for each
        // of the remaining k-1 operations, but we need to subtract the cases where s == t initially.
        long long ways = 1;
        int same = strcmp(s, t) == 0 ? 1 : 0;
        for (int i = 0; i < k - 1; ++i) {
            ways = (ways * n) % MOD;
        }
        ways = (ways * (n - 1)) % MOD;
        if (same && k % 2 == 0) {
            ways = (ways + MOD - 1) % MOD; // Subtract the case where no operation is needed
        }
        return (int)ways;
    }
}
