func numberOfWays(s string, t string, k int64) int {
    const mod = 1000000007
    n := len(s)

    if k == 1 {
        // No way to transform s into t in exactly one move if they are not initially the same
        return 0
    }

    // Count matching characters at each position after cyclic shift
    match := 0
    for i := 0; i < n; i++ {
        if s == t {
            match++
        }
        s = s[n-1:] + s[:n-1] // Perform cyclic shift to the left
    }

    if match == 0 {
        // If s cannot be transformed into t by any cyclic shift
        return 0
    } else if match == 1 {
        if k%2 == 0 {
            // If they can be matched in an even number of operations, there's exactly one way
            return 1
        }
        // If an odd number of operations are required but we have exactly one match, it's impossible
        return 0
    } else {
        // For k > 2 and multiple matches, there are always 2 ways to achieve the transformation
        // in an even number of steps, and no way in an odd number of steps
        if k%2 == 1 {
            return 2
        } else {
            if k == 2 {
                return match // When k=2, the number of ways directly equals the number of matches
            }
            return 0
        }
    }
}
