class Solution {
    public int numberOfWays(String s, String t, long k) {
        final int MOD = 1000000007;
        if (s.equals(t)) {
            if (k % 2 == 0) {
                return 1;
            } else {
                return (int) ((k * (s.length() - 1)) % MOD);
            }
        } else {
            if (k == 1) return 0;
            if (s.length() == 2) {
                if (k % 2 == 0) return 1;
                else return 0;
            }
            if (k % 2 == 0) {
                if ((k / 2) % 2 == 0) return 2;
                else return 0;
            } else {
                return (int) ((k * (s.length() - 1)) % MOD);
            }
        }
    }
}
