class Solution {

    /**
     * @param String $s
     * @param String $t
     * @param Integer $k
     * @return Integer
     */
    function numberOfWays($s, $t, $k) {
        $mod = 1000000007;
        $n = strlen($s);
        if ($s === $t && $k % 2 === 0) return ($n - 1) % $mod;
        if ($s === $t && $k % 2 === 1) return 2 % $mod;
        if ($k === 1) return 0;

        $diff = 0;
        for ($i = 0; $i < $n; $i++) {
            if ($s[$i] !== $t[$i]) {
                $diff = $n - $i;
                break;
            }
        }

        if ($diff === 0) return ($n - 1) % $mod;
        if ($k === 2) return 2 % $mod;

        $ways = 2; // Always two ways for k >= 3
        return $ways % $mod;
    }
}
