class Solution(object):
    def numberOfWays(self, s, t, k):
        mod = 10**9 + 7
        n = len(s)
        if s == t:
            return (k * (n - 1)) % mod if k % 2 == 0 else 2 % mod
        if k == 1:
            return 0
        if s[::-1] == t:
            if k % 2 == 1:
                return 0
            else:
                return (k - 1) * (n - 1) % mod
        prefix = 0
        for i in range(n):
            if s[i] != t[i]:
                break
            prefix += 1
        suffix = 0
        for i in range(n):
            if s[n - 1 - i] != t[n - 1 - i]:
                break
            suffix += 1
        if prefix + suffix >= n:
            return (k - 2) * (n - 1) % mod if k > 2 else 0
        return 0
