class Solution(object):
    def numberOfWays(self, s, t, k):
        """
        :type s: str
        :type t: str
        :type k: int
        :rtype: int
        """
        mod = 10**9 + 7

        def gcd(a, b):
            while b:
                a, b = b, a % b
            return a

        if s == t:
            if k == 1:
                return 0
            if k % 2 == 1:
                return 2
            else:
                ways = 2
                for i in range(2, k, 2):
                    ways = (ways + pow(i, mod - 2, mod) * (len(s) - 1)) % mod
                return ways

        if k == 1:
            return 0
        if k == 2:
            return (len(s) - 1) if s != t else 0

        # Count the number of positions where s and t differ
        diff_count = sum([1 for i, j in zip(s, t) if i != j])

        # If s can be transformed into t by rotating
        if diff_count == 0 or gcd(len(s), diff_count) > 1:
            return 0

        # For k > 2 and s != t, the operation is not possible
        return 0
