impl Solution {
    pub fn number_of_ways(s: String, t: String, k: i64) -> i32 {
        const MOD: i64 = 1_000_000_007;
        let n = s.len() as i64;
        if s == t {
            if k == 1 {
                return 0;
            } else if k % 2 == 0 {
                return (((n - 1) * (k - 1) % MOD) + 2) as i32;
            } else {
                return 2;
            }
        } else {
            if k == 1 {
                return 0;
            } else if k == 2 {
                let mut res = 0;
                for i in 1..s.len() {
                    if &s[i..] == &t[..t.len()-i] && &s[..i] == &t[t.len()-i..] {
                        res += 1;
                    }
                }
                return res;
            } else {
                if k % 2 == 0 {
                    return 2;
                } else {
                    let mut res = 0;
                    for i in 1..s.len() {
                        if &s[i..] == &t[..t.len()-i] && &s[..i] == &t[t.len()-i..] {
                            res += 1;
                        }
                    }
                    if res > 0 {
                        return (((n - 1) * (k - 1) % MOD) + 2) as i32;
                    } else {
                        return 0;
                    }
                }
            }
        }
    }
}
