object Solution {
    def numberOfWays(s: String, t: String, k: Long): Int = {
        val mod = 1000000007
        if (s == t) {
            if (k == 1) return 0
            if (k % 2 == 0) {
                if (k == 2 && s.indices.exists(i => s.drop(i) + s.take(i) == t)) return 2
                return ((k - 1) % mod).toInt
            }
            return 2
        }

        if (k == 1) 0
        else {
            val isPossibleInOneMove = s.indices.exists(i => s.drop(i) + s.take(i) == t)
            k match {
                case 2 if isPossibleInOneMove => 2
                case _ if k % 2 == 0 => 2
                case _ => 0
            }
        }
    }
}
