class Solution {
    func numberOfWays(_ s: String, _ t: String, _ k: Int) -> Int {
        let mod = 1_000_000_007
        if s == t {
            if k % 2 == 0 {
                return ((k - 1) % mod * (s.count % mod)) % mod
            } else {
                if k == 1 { return 1 }
                else { return 0 }
            }
        } else {
            if k == 1 {
                return 0
            } else if k == 2 {
                return s == String(t.reversed()) ? 0 : 2
            } else {
                if k % 2 == 1 {
                    return 0
                } else {
                    return ((k - 2) % mod * (s.count % mod)) % mod
                }
            }
        }
    }
}
