function numberOfWays(s: string, t: string, k: number): number {
    const MOD = 1e9 + 7;
    let same = 0;
    for (let i = 0; i < s.length; i++) {
        if (s[i] === t[i]) same++;
    }
    let result = 0;
    for (let i = 0; i <= k; i++) {
        for (let j = 0; j <= i; j++) {
            if ((j + same) % 2 === i % 2 && j + same <= i) {
                let temp = 1;
                for (let l = 1; l <= i; l++) {
                    temp = (temp * (s.length - l + 1)) % MOD;
                    temp = (temp * modInverse(l, MOD)) % MOD;
                }
                result = (result + temp) % MOD;
            }
        }
    }
    return result;
}

function modInverse(a: number, mod: number): number {
    let m0 = mod;
    let y = 0, x = 1;
    if (mod === 1) return 0;
    while (a > 1) {
        let q = Math.floor(a / mod);
        let t = mod;
        mod = a % mod;
        a = t;
        t = y;
        y = x - q * y;
        x = t;
    }
    if (x < 0) x += m0;
    return x;
}
