public class Solution {
    public int SumCounts(int[] nums) {
        const int mod = 1000000007;
        long ans = 0;
        int n = nums.Length;
        Dictionary<int, int> lastPos = new Dictionary<int, int>();
        int[] prev = new int[n];
        for (int i = 0; i < n; i++) {
            if (lastPos.ContainsKey(nums[i])) {
                prev[i] = lastPos[nums[i]] + 1;
            } else {
                prev[i] = 0;
            }
            lastPos[nums[i]] = i;
        }
        int[] next = new int[n];
        lastPos.Clear();
        for (int i = n - 1; i >= 0; i--) {
            if (lastPos.ContainsKey(nums[i])) {
                next[i] = lastPos[nums[i]] - 1;
            } else {
                next[i] = n - 1;
            }
            lastPos[nums[i]] = i;
        }
        for (int i = 0; i < n; i++) {
            long left = i - prev[i] + 1;
            long right = next[i] - i + 1;
            ans = (ans + left * right) % mod;
        }
        return (int)ans;
    }
}
