public class Solution {
    public int SumCounts(int[] nums) {
        long mod = 1000000007;
        int n = nums.Length;
        Dictionary<int, int> last = new Dictionary<int, int>();
        int[] left = new int[n];
        long result = 0;

        for (int i = 0; i < n; ++i) {
            if (last.ContainsKey(nums[i])) {
                left[i] = last[nums[i]] + 1;
            }
            last[nums[i]] = i;
        }

        last.Clear();
        int[] right = new int[n];
        for (int i = n - 1; i >= 0; --i) {
            if (last.ContainsKey(nums[i])) {
                right[i] = last[nums[i]] - 1;
            } else {
                right[i] = n - 1;
            }
            last[nums[i]] = i;
        }

        for (int i = 0; i < n; ++i) {
            long lCount = i - left[i] + 1;
            long rCount = right[i] - i + 1;
            result = (result + lCount * rCount) % mod;
        }

        return (int)result;
    }
}
