#include <vector>
#include <unordered_map>

using namespace std;

class Solution {
public:
    int sumCounts(vector<int>& nums) {
        const int mod = 1e9 + 7;
        long long answer = 0;
        unordered_map<int, int> lastIndex;
        vector<int> contribution(nums.size() + 1, 0);

        for (int i = 0, start = 0; i < nums.size(); ++i) {
            if (lastIndex.count(nums[i])) {
                start = max(start, lastIndex[nums[i]] + 1);
            }
            contribution[i + 1] = (contribution[i] + i + 1 - start) % mod;
            answer = (answer + contribution[i + 1]) % mod;
            lastIndex[nums[i]] = i;
        }

        return answer;
    }
};
