#include <stdlib.h>

int sumCounts(int* nums, int numsSize) {
    const int mod = 1e9 + 7;
    long long result = 0;
    int* last = (int*)malloc((numsSize + 1) * sizeof(int));
    int* contribution = (int*)malloc((numsSize + 1) * sizeof(int));
    for (int i = 0; i <= numsSize; ++i) {
        last[i] = -1;
        contribution[i] = 0;
    }

    for (int i = 0; i < numsSize; ++i) {
        contribution[i + 1] = (contribution[i] + i - last[nums[i]]) % mod;
        result = (result + contribution[i + 1]) % mod;
        last[nums[i]] = i;
    }

    free(last);
    free(contribution);
    return (int)result;
}
