#include <stdio.h>
#include <string.h>

#define MOD 1000000007

int sumCounts(int* nums, int numsSize) {
    long long ans = 0;
    int last[100001];
    memset(last, -1, sizeof(last));

    for (int i = 0; i < numsSize; ++i) {
        for (int j = nums[i]; j <= 100000; ++j) {
            if (last[j] != -1) break;
            last[j] = i;
        }
    }

    memset(last, -1, sizeof(last));
    for (int i = 0; i < numsSize; ++i) {
        ans = (ans + (long long)(i - last[nums[i]]) * (numsSize - i) % MOD) % MOD;
        last[nums[i]] = i;
    }

    return (int)ans;
}
