class Solution {
  int sumCounts(List<int> nums) {
    const int mod = 1000000007;
    Map<int, List<int>> lastIndex = {};
    for (int i = 0; i < nums.length; i++) {
      lastIndex.putIfAbsent(nums[i], () => []).add(i);
    }

    List<int> contribution = List.filled(nums.length, 0);
    for (var indexes in lastIndex.values) {
      for (int i = 0; i < indexes.length; i++) {
        int start = i > 0 ? indexes[i - 1] : -1;
        int end = i < indexes.length - 1 ? indexes[i + 1] : nums.length;
        contribution[indexes[i]] = (indexes[i] - start) * (end - indexes[i]);
      }
    }

    int result = 0;
    for (int i = 0; i < nums.length; i++) {
      result = (result + contribution[i]) % mod;
    }
    return result;
  }
}
