class Solution {
  int sumCounts(List<int> nums) {
    final int mod = 1000000007;
    int n = nums.length;
    Map<int, int> lastPos = {};
    List<int> left = List.filled(n, 0);
    for (int i = 0; i < n; i++) {
      left[i] = lastPos.containsKey(nums[i]) ? lastPos[nums[i]]! + 1 : 0;
      lastPos[nums[i]] = i;
    }

    lastPos.clear();
    List<int> right = List.filled(n, 0);
    for (int i = n - 1; i >= 0; i--) {
      right[i] = lastPos.containsKey(nums[i]) ? lastPos[nums[i]]! - 1 : n - 1;
      lastPos[nums[i]] = i;
    }

    int result = 0;
    for (int i = 0; i < n; i++) {
      int contrib = ((i - left[i] + 1) * (right[i] - i + 1)) % mod;
      result = (result + contrib) % mod;
    }
    return result;
  }
}
