class Solution {
    fun sumCounts(nums: IntArray): Int {
        val mod = 1000000007
        var sum = 0L
        val freq = mutableMapOf<Int, Int>()
        var prefixSum = 0
        val contribution = IntArray(nums.size + 1)

        for (num in nums) {
            freq[num] = (freq[num] ?: 0) + 1
            if (freq[num] == 1) {
                prefixSum++
            }
            contribution[prefixSum]++
        }

        for (i in contribution.indices) {
            sum = (sum + 1L * contribution[i] * i * i) % mod
        }

        return sum.toInt()
    }
}
