class Solution {
    fun sumCounts(nums: IntArray): Int {
        val mod = 1000000007
        val lastPosition = IntArray(100001) { -1 }
        val contribution = LongArray(nums.size + 1)
        var total = 0L
        var sum = 0L

        for (i in nums.indices) {
            val num = nums[i]
            sum += i - lastPosition[num]
            contribution[i + 1] = sum
            lastPosition[num] = i
        }

        for (i in contribution.indices) {
            total = (total + contribution[i]) % mod
        }

        return total.toInt()
    }
}
