class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function sumCounts($nums) {
        $mod = 1000000007;
        $n = count($nums);
        $lastPosition = [];
        $result = 0;
        $contrib = 0;

        for ($i = 0; $i < $n; $i++) {
            if (isset($lastPosition[$nums[$i]])) {
                $contrib = ($contrib + ($i - $lastPosition[$nums[$i]]) * ($i - $lastPosition[$nums[$i]] + 1) / 2) % $mod;
            } else {
                $contrib = ($contrib + ($i + 1) * ($i + 2) / 2) % $mod;
            }
            $lastPosition[$nums[$i]] = $i;
            $result = ($result + $contrib) % $mod;
        }

        return $result;
    }
}
