class Solution {
    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function sumCounts($nums) {
        $mod = 1000000007;
        $n = count($nums);
        $ans = 0;
        $last = array_fill(0, $n, -1);
        $next = array_fill(0, $n, $n);
        $map = [];

        for ($i = 0; $i < $n; ++$i) {
            if (isset($map[$nums[$i]])) {
                $last[$i] = $map[$nums[$i]];
            }
            $map[$nums[$i]] = $i;
        }

        $map = [];

        for ($i = $n - 1; $i >= 0; --$i) {
            if (isset($map[$nums[$i]])) {
                $next[$i] = $map[$nums[$i]];
            }
            $map[$nums[$i]] = $i;
        }

        for ($i = 0; $i < $n; ++$i) {
            $ans = ($ans + ($i - $last[$i]) * ($next[$i] - $i) % $mod) % $mod;
        }

        return $ans;
    }
}
