class Solution {

    /**
     * @param Integer[] $nums
     * @return Integer
     */
    function sumCounts($nums) {
        $mod = 1000000007;
        $n = count($nums);
        $answer = 0;
        $freq = array_fill(0, 100001, 0);
        $left = array_fill(0, $n, 0);
        $right = array_fill(0, $n, 0);
        $j = 0;

        for ($i = 0; $i < $n; ++$i) {
            if ($freq[$nums[$i]] == 0) {
                $left[$i] = ($i - $j + 1);
            } else {
                $left[$i] = $i - $j;
            }
            $freq[$nums[$i]]++;
            while ($freq[$nums[$i]] > 1) {
                $freq[$nums[$j]]--;
                $j++;
            }
        }

        $freq = array_fill(0, 100001, 0);
        $j = $n - 1;

        for ($i = $n - 1; $i >= 0; --$i) {
            if ($freq[$nums[$i]] == 0) {
                $right[$i] = ($j - $i + 1);
            } else {
                $right[$i] = $j - $i;
            }
            $freq[$nums[$i]]++;
            while ($freq[$nums[$i]] > 1) {
                $freq[$nums[$j]]--;
                $j--;
            }
        }

        for ($i = 0; $i < $n; ++$i) {
            $answer = ($answer + $left[$i] * $right[$i]) % $mod;
        }

        return $answer;
    }
}
