class Solution:
    def sumCounts(self, nums: list[int]) -> int:
        MOD = 10**9 + 7
        answer = 0
        stack = []
        prev = [0] * len(nums)
        for i, num in enumerate(nums):
            while stack and num >= nums[stack[-1]]:
                stack.pop()
            prev[i] = stack[-1] if stack else -1
            stack.append(i)

        stack = []
        next_ = [0] * len(nums)
        for i in range(len(nums) - 1, -1, -1):
            while stack and num > nums[stack[-1]]:
                stack.pop()
            next_[i] = stack[-1] if stack else len(nums)
            stack.append(i)

        for i in range(len(nums)):
            answer += nums[i] * (i - prev[i]) * (next_[i] - i)
            answer %= MOD

        return answer
