use std::collections::HashMap;

impl Solution {
    pub fn sum_counts(nums: Vec<i32>) -> i32 {
        let mut last_indices = HashMap::new();
        let mut contributions = vec![0; nums.len()];
        let modulo = 1_000_000_007;

        for (i, &num) in nums.iter().enumerate() {
            if let Some(&last_index) = last_indices.get(&num) {
                contributions[i] = contributions[last_index] + (i - last_index) as i64;
            } else {
                contributions[i] = i as i64 + 1;
            }
            last_indices.insert(num, i);
        }

        let mut result = 0i64;
        let mut prefix_sums = vec![0i64; nums.len() + 1];
        for i in 0..nums.len() {
            prefix_sums[i + 1] = (prefix_sums[i] + contributions[i]) % modulo;
        }

        for &contribution in contributions.iter() {
            result = (result + contribution * contribution) % modulo;
        }

        for i in 1..=nums.len() {
            result = (result + (prefix_sums[i] * prefix_sums[i] - prefix_sums[i]) % modulo) % modulo;
        }

        result as i32
    }
}
