impl Solution {
    pub fn sum_counts(nums: Vec<i32>) -> i32 {
        let modulo = 1_000_000_007;
        let mut result = 0i64;
        let mut last_pos = std::collections::HashMap::new();
        let mut sum = 0i64;
        let mut sum_sq = 0i64;

        for (i, &num) in nums.iter().enumerate() {
            let i = i as i64;
            if let Some(&last) = last_pos.get(&num) {
                sum = (sum + (i - last) * (i + 1 - last) / 2 % modulo) % modulo;
            } else {
                sum = (sum + (i + 1) * (i + 2) / 2 % modulo) % modulo;
            }
            sum_sq = (sum_sq + sum) % modulo;
            last_pos.insert(num, i + 1);
        }

        result = (sum_sq % modulo + modulo) % modulo;
        result as i32
    }
}
