object Solution {
    def sumCounts(nums: Array[Int]): Int = {
        val mod = 1000000007
        var sum = 0L
        val lastIndices = scala.collection.mutable.Map[Int, Int]()
        val contribs = Array.fill(nums.length)(0L)

        for (i <- nums.indices) {
            contribs(i) = if (i > 0) contribs(i - 1) + i else i
            lastIndices.get(nums(i)) match {
                case Some(lastIndex) => contribs(i) -= (lastIndex + 1)
                case None => 
            }
            lastIndices(nums(i)) = i
        }

        for (contrib <- contribs) sum = (sum + contrib + 1) % mod
        sum.toInt
    }
}
