object Solution {
    def sumCounts(nums: Array[Int]): Int = {
        val mod = 1000000007
        val n = nums.length
        var answer = 0L
        val lastPosition = scala.collection.mutable.Map[Int, Int]()
        val prefixSum = Array.ofDim[Long](n + 1)

        for (i <- 0 until n) {
            prefixSum(i + 1) = prefixSum(i) + (lastPosition.get(nums(i)) match {
                case Some(pos) => i - pos
                case None => i + 1
            })
            lastPosition(nums(i)) = i
        }

        for (i <- 1 to n) {
            answer = (answer + prefixSum(i)) % mod
        }

        answer.toInt
    }
}
