object Solution {
    def sumCounts(nums: Array[Int]): Int = {
        val mod = 1000000007
        var sum = 0L
        val freq = scala.collection.mutable.Map[Int, Int]()
        var total = 0
        var distinctCount = 0

        for (i <- nums.indices) {
            if (!freq.contains(nums(i))) {
                freq(nums(i)) = 1
                distinctCount += 1
            } else {
                val prevCount = freq(nums(i))
                total -= prevCount * prevCount
                freq(nums(i)) = prevCount + 1
            }
            total += freq(nums(i)) * freq(nums(i))
            sum = (sum + total) % mod
        }

        sum.toInt
    }
}
