function sumCounts(nums: number[]): number {
    const mod = 1e9 + 7;
    let result = 0;
    const lastIndex = new Map<number, number>();
    const contrib = new Array(nums.length).fill(0);
    let total = 0;

    for (let i = 0; i < nums.length; i++) {
        if (lastIndex.has(nums[i])) {
            const j = lastIndex.get(nums[i])!;
            contrib[i] = (contrib[j] + (i - j)) % mod;
        } else {
            contrib[i] = (i + 1) % mod;
        }
        total = (total + contrib[i]) % mod;
        lastIndex.set(nums[i], i);
    }

    for (let i = 0; i < nums.length; i++) {
        result = (result + total) % mod;
        total = (total - contrib[i]) % mod;
        if (total < 0) total += mod;
    }

    return result;
}
