function sumCounts(nums: number[]): number {
    const mod = 1e9 + 7;
    let result = 0;
    const freq = new Map<number, number>();
    let add = 0, distinct = 0;

    nums.forEach((num, i) => {
        if (!freq.has(num) || freq.get(num) === 0) {
            freq.set(num, 1);
            distinct++;
            add = (add + distinct) % mod;
        } else {
            let prevIndex = i - 1;
            while (nums[prevIndex] !== num) prevIndex--;
            const distance = i - prevIndex;
            const temp = (distinct - 1) * distance + 1;
            add = (add + temp) % mod;
        }
        result = (result + add) % mod;
    });

    return result;
}
