#include <vector>
#include <algorithm>

using namespace std;

class Solution {
public:
    int sumImbalanceNumbers(vector<int>& nums) {
        int res = 0, n = nums.size();
        for (int i = 0; i < n; ++i) {
            vector<int> minLeft(n), minRight(n), maxLeft(n), maxRight(n);
            minLeft[i] = maxLeft[i] = i;
            for (int j = i + 1; j < n; ++j) {
                minLeft[j] = nums[j] < nums[minLeft[j - 1]] ? j : minLeft[j - 1];
                maxLeft[j] = nums[j] > nums[maxLeft[j - 1]] ? j : maxLeft[j - 1];
            }
            minRight[i] = maxRight[i] = i;
            for (int j = i - 1; j >= 0; --j) {
                minRight[j] = nums[j] <= nums[minRight[j + 1]] ? j : minRight[j + 1];
                maxRight[j] = nums[j] >= nums[maxRight[j + 1]] ? j : maxRight[j + 1];
            }
            for (int j = i; j < n; ++j) {
                int minVal = nums[min(minLeft[j], minRight[j])];
                int maxVal = nums[max(maxLeft[j], maxRight[j])];
                res += maxVal - minVal - (j - i + 1) + 1;
            }
        }
        return res;
    }
};
