class Solution {
    fun sumImbalanceNumbers(nums: IntArray): Int {
        var ans = 0
        val n = nums.size
        for (i in 0 until n) {
            val minStack = mutableListOf<Int>()
            val maxStack = mutableListOf<Int>()
            for (j in i until n) {
                var count = 1
                while (minStack.isNotEmpty() && nums[minStack.last()] > nums[j]) {
                    minStack.removeAt(minStack.size - 1)
                    count++
                }
                ans -= count * (nums[j] - (minStack.lastOrNull()?.let { nums[it] } ?: nums[j]))
                minStack.add(j)

                count = 1
                while (maxStack.isNotEmpty() && nums[maxStack.last()] < nums[j]) {
                    maxStack.removeAt(maxStack.size - 1)
                    count++
                }
                ans += count * (nums[j] - (maxStack.lastOrNull()?.let { nums[it] } ?: nums[j]))
                maxStack.add(j)
            }
        }
        return ans
    }
}
