public class Solution {
    public bool CheckArray(int[] nums, int k) {
        int[] diff = new int[nums.Length];
        int cumSum = 0; // To track the cumulative sum of differences

        for (int i = 0; i < nums.Length; i++) {
            cumSum += diff[i]; // Update cumSum with the current difference

            int actualValue = nums[i] - cumSum; // Calculate the adjusted value of nums[i]
            if (actualValue > 0) {
                int decreaseAmount = actualValue;
                diff[i] += decreaseAmount; // Apply decrease to current position
                if (i + k < nums.Length) {
                    diff[i + k] -= decreaseAmount; // Reverse the decrease beyond the subarray
                }
                cumSum += decreaseAmount; // Update cumSum with the new decrease
            } else if (actualValue < 0) {
                // It's not possible to make all elements 0
                return false;
            }
        }

        // If we reach here, it means it's possible to make all elements 0
        return true;
    }
}
