public class Solution {
    public bool CheckArray(int[] nums, int k) {
        if (nums.Length < k) return false; // Early exit if k is larger than array size

        long extraOps = 0; // Track extra operations needed due to previous decreases
        for (int i = 0; i < nums.Length; i++) {
            nums[i] -= (int)extraOps; // Apply any extra operations to the current element

            if (nums[i] < 0) return false; // If any element goes below 0, return false

            if (i + k <= nums.Length) { // Only proceed if there's room for a k-length subarray
                if (nums[i] > 0) {
                    if (i + k == nums.Length) { // Special handling for the last possible subarray
                        extraOps += nums[i];
                    } else {
                        extraOps += nums[i];
                        // Ensure the extra operations are reverted at the end of the subarray's reach
                        if (i + k < nums.Length) nums[i + k] -= nums[i];
                    }
                }
            }
        }

        // Check if the last few elements are zeroes (as the loop might not zero them out)
        for (int i = nums.Length - k + 1; i < nums.Length; i++) {
            if (nums[i] != 0) return false;
        }

        return true;
    }
}
