public class Solution {
    public bool CheckArray(int[] nums, int k) {
        long neededOps = 0; // Total operations needed so far
        Queue<long> ops = new Queue<long>(); // Queue to track operations over the next k elements

        for (int i = 0; i < nums.Length; i++) {
            if (ops.Count == k) {
                neededOps -= ops.Dequeue(); // Remove the effect of the operation that no longer affects the current element
            }

            nums[i] -= (int)neededOps; // Apply all previous operations to the current element

            if (nums[i] < 0) {
                // Cannot make an element less than zero, so return false
                return false;
            } else if (nums[i] > 0) {
                // If the current element is greater than 0, calculate and queue the needed operations
                ops.Enqueue(nums[i]);
                neededOps += nums[i]; // Update the total operations needed
            }
        }

        // Check if there are elements that cannot be zeroed out
        foreach (var extra in ops) {
            if (extra > 0) {
                // If any operation in the queue is still positive, it means not all elements can be zeroed out
                return false;
            }
        }

        // If all checks pass, then it's possible to zero out the array
        return true;
    }
}
