#include <vector>
using namespace std;

class Solution {
public:
    bool checkArray(vector<int>& nums, int k) {
        int n = nums.size();
        int carry = 0; // To keep track of decrements needed due to previous elements.
        for (int i = 0; i < n; ++i) {
            nums[i] -= carry; // Apply the decrements accumulated from previous operations.
            if (nums[i] < 0) return false; // If any element goes negative, it's not possible.
            carry -= min(nums[i], carry); // Adjust carry for the decrements already applied.
            if (i + k < n) {
                carry += nums[i]; // Add current element's value to carry for next k elements.
                nums[i + k] -= nums[i]; // Apply decrement in advance for the k-th next element.
            } else if (nums[i] > 0) { // If there are not enough elements left to decrement.
                return false;
            }
        }
        return true;
    }
};
